para.programlisting:
#!/bin/sh # # local_fs - check and mount local filesystems # PATH=/sbin:/bin ; export PATH
case $1 in
start) echo "Checking local filesystem integrity." fsck -ATCp if [ $(($?)) -gt $((1)) ]; then echo "Filesystem errors still exist! Manual intervention required." /bin/sh else echo "Remounting / as read-write." mount -n -o remount,rw / echo -n > /etc/mtab mount -f -o remount,rw / echo "Mounting local filesystems." mount -a -t nonfs,smbfs fi ;;
stop) echo "Unmounting local filesystems." umount -a -r ;;
*) echo "usage: $0 start|stop"; ;;
esac # # end of local_fs
