#!/bin/bash # Find suid root files. # A strange suid file might indicate a security hole, #+ or even a system intrusion.
directory="/usr/sbin" # Might also try /sbin, /bin, /usr/bin, /usr/local/bin, etc. permissions="+4000" # suid root (dangerous!)
for file in $( find "$directory" -perm "$permissions" ) do ls -ltF --author "$file" done

