# cat /etc/radvd.conf
interface eth1                     
{                                   
      AdvSendAdvert on;
      AdvIntervalOpt on;            

      MinRtrAdvInterval 3;         
      MaxRtrAdvInterval 10;        
      AdvHomeAgentFlag off;        

      prefix fec0:106:1100::/64    
      {                           
          AdvOnLink on;           
          AdvAutonomous on;        
          AdvRouterAddr on;       
      };                         
};

# cat /etc/radvd.conf
interface eth1
{
   AdvSendAdvert on;
   MaxRtrAdvInterval 3;
   MinRtrAdvInterval 1;    
   AdvIntervalOpt off;
   AdvHomeAgentFlag on;
   HomeAgentLifetime 10000;
   HomeAgentPreference 20;
   AdvHomeAgentInfo on;
   prefix fec0:106:2700::2/64
   {
     AdvRouterAddr on;
     AdvOnLink on;
     AdvAutonomous on;
     AdvPreferredLifetime 10000;
     AdvValidLifetime 12000;
   };
 };
