sect1.programlisting:
#!/bin/sh # Please place this file /etc/hotplug # # input-specific hotplug policy agent. # # Kernel Input params are: # # ACTION=add # PHYS=pysical location of device # NAME=Name of the device # # HISTORY: # 15-JUN-2003 removed paste # 07-MAY-2003 remake by Aivils Stoss # /proc manipulation added # parse kbd.conf event.conf mouse.conf. # create necessary symbolic links # 28-SEP-2002 Initial version from Andreas Schuldei # andreas (at) schuldei.org # cd /etc/hotplug . hotplug.functions DEBUG=yes export DEBUG
KBD_CONFIG="./kbd.conf" EVENT_CONFIG="./event.conf" MOUSE_CONFIG="./mouse.conf"
setup_kbd () { while read VT_NUM PHYS_PATTERN NAME_PATTERN do if [ `echo "$VT_NUM" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then VT=`echo "$VT_NUM" | sed 's/VT//' | awk '{printf "%02d", $0}'` if [ -d /proc/bus/console/$VT ]; then echo "$PHYS" > "/proc/bus/console/$VT/keyboard" debug_mesg "Input device $NAME on $PHYS mapping as secondary to VT$VT" return; else debug_mesg "Trying to configure keyboard for VT$VT , but not enough VT's available" fi fi done debug_mesg "Found no fitting VT" }
setup_event () { while read SYM_LINK PHYS_PATTERN NAME_PATTERN do if [ `echo "$SYM_LINK" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then case $ACTION in add) cd /dev/input rm -f $SYM_LINK ln -s $DEV_EVENT $SYM_LINK debug_mesg "Input event device $NAME on $PHYS linked to $SYM_LINK" ;; remove) rm -f /dev/input/$SYM_LINK debug_mesg "Input event device link $SYM_LINK removed" ;; esac return; fi done debug_mesg "Found no fitting event device" }
setup_mouse () { while read SYM_LINK PHYS_PATTERN NAME_PATTERN do if [ `echo "$SYM_LINK" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then case $ACTION in add) cd /dev/input rm -f $SYM_LINK ln -s $DEV_MOUSE $SYM_LINK debug_mesg "Input mouse device $NAME on $PHYS linked to $SYM_LINK" ;; remove) rm -f /dev/input/$SYM_LINK debug_mesg "Input mouse device link $SYM_LINK removed" ;; esac return; fi done debug_mesg "Found no fitting mouse device" }
if [ "$ACTION" = "" ]; then mesg Bad input agent invocation exit 1 fi
DEV_HANDLERS=`grep -E 'Phys|Handlers' /proc/bus/input/devices | \ awk '{ if(count == 0) { printf("%s\t",$0); count++; } else { print $0; count=0; } }' | \ grep $PHYS | awk -F\t '{print $2}' | sed 's/^.*=//'`
# older grep do not support -o :-( #DEV_EVENT=`echo $MATCHED | grep -oE event[0-9]+` #DEV_MOUSE=`echo $MATCHED | grep -oE mouse[0-9]+` #DEV_KBD=`echo $MATCHED | grep -o kbd`
DEV_EVENT=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /event/) print $n}'` DEV_MOUSE=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /mouse/) print $n}'` DEV_KBD=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /kbd/) print $n}'`
# # What to do with this input device event? # case "$ACTION" in
add) if [ -n "$DEV_KBD" ]; then setup_kbd < $KBD_CONFIG fi if [ -n "$DEV_EVENT" ]; then setup_event < $EVENT_CONFIG fi if [ -n "$DEV_MOUSE" ]; then setup_mouse < $MOUSE_CONFIG fi ;; remove) #setup_event < $EVENT_CONFIG #setup_mouse < $MOUSE_CONFIG ;; *) debug_mesg "Input '$ACTION' event not supported" return 1 ;;
esac

#!/bin/bash

cd /etc/hotplug
. hotplug.functions
#DEBUG=yes export DEBUG

export ARG_SEP='\002'
export VAL_SEP='\003'

for dev in `ls /tmp/.X11-unix/ | grep evdev`; do {
    export dev="/tmp/.X11-unix/${dev}"
    /bin/sh -c 'echo -e "2.1${ARG_SEP}NAME${VAL_SEP}${NAME}${ARG_SEP}PHYS${VAL_SEP}${PHYS}${ARG_SEP}ACTION${VAL_SEP}${ACTION}" >> ${dev}' &
}; done

#!/bin/sh # # ruby_init This scripts configures cold-pluging for bruby, \ # loads additional input modules, manages the XFree \ # and display manager configuration files # # chkconfig: 2345 02 98 # description: Configures the Bruby input subsystem and manages \ # XFree and display manager configuration files. # config: /etc/sysconfig/ruby.conf # # TODO # * handle commented out "PciOsConfig" # *? status/ ext-status # - show config # - check wether curr. configuration matches setup ? # - make it work when hotplug files not installed ? # ( currently it will just just inform that they are not installed # and exit ) # # $Id$ # - don't exit silently if hackvideo or modules loading is deactivated, # run the script to the end # $Id$ # - show keyboard status per VT ,use sed instead of head(head is in /usr) # - *? status/ ext-status # - (mice & evdev links)? # $Id$ # - include hackvideo handling (long time wondering what was missing) # $Id$ # - got it actually running # - use >& /dev/null to load variables from the config file # (what a mess) # $Id$ # - initial release
PATH=/sbin:/bin:/usr/sbin:/usr/bin
# source function library if [ -f /etc/init.d/functions ]; then . /etc/init.d/functions elif [ -f /etc/rc.d/init.d/functions ]; then . /etc/rc.d/init.d/functions fi
# source defaults if [ -f /etc/sysconfig/ruby.conf ]; then #WTF, how to get rid of the ".... >& /dev/null" . /etc/sysconfig/ruby.conf >& /dev/null RUBY_CONF="/etc/sysconfig/ruby.conf" else action "ruby_init: Configuration file missing" /bin/false exit 1 fi
function get_status() { run_hackvideo status # prefbus_used ? # print dumb_con=? get_kbds #and curr. active VTs }
function get_ext_status() { run_input_rc status run_hackvideo status # prefbus_used ? # print dumb_con=? get_kbds #and curr. active VTs get_links mouse get_links event }
function run_start_restart() { load_modules run_hackvideo start run_input_rc start run_auto_dm run_auto_xfree }
function run_input_rc () { for RC in /etc/hotplug/input.rc do eval "doit=\"\$HOTPLUG_RC_input\"" if [ "$doit" != yes -a "$1" != status ]; then continue fi $RC $1 if [ "$1" != status ]; then action "bruby: configuring cold-pluged devices." $RC $1 fi done }
function run_hackvideo () { eval "doit=\"\$HACK_VIDEO_ENABLE\"" if [ "$doit" != yes ]; then if [ "$1" = status ]; then echo "XFree hackvideo not configured." fi return ; fi if [ -f /proc/bus/pci/hackvideo ]; then if [ -f /etc/X11/XF86Config-4 ]; then xf_file="/etc/X11/XF86Config-4" elif [ -f /etc/X11/XF86Config ]; then xf_file="/etc/X11/XF86Config" else action "bruby: XFree configuration file not found" /bin/false exit 1 fi #fi eval "xf_hackvideo=`sed -n '/^#/d;s/^.*"PciOsConfig"[ ]//p' $xf_file | sed -e 's/[ ]//g'`" if [ "$xf_hackvideo" = "1" ]; then case "$1" in start) action "bruby: Enabling XFree hackvideo workaound." /bin/true /bin/echo "1" > /proc/bus/pci/hackvideo ;; stop) action "bruby: Disabling XFree hackvideo workaound." /bin/echo "0" > /proc/bus/pci/hackvideo ;; status) eval "hack_enabled=`cat /proc/bus/pci/hackvideo`" if [ $hack_enabled = 1 ]; then echo "XFree hackvideo activated" else echo "XFree hackvideo not activated," echo "but enabled in configuration. " fi ;; *) ;; esac else action "bruby: Hackvideo not configured in XFree," /bin/false action "bruby: but enabled in $RUBY_CONF." /bin/false
fi else action "bruby: XFree hackvideo configured," /bin/false action "bruby: but kernel hackvideo support missing." /bin/false fi
}
function load_modules () { eval "doit=\"\$LOAD_MODULES\"" if [ "$doit" != yes -a "$1" != status ]; then return ; fi action "bruby: loading additional modules." /bin/true cat $RUBY_CONF | sed -n "/input modules /,/config/p" | while read module args do case "$module" in \#*|"") continue ;; esac initlog -s "Loading module: $module" modprobe $module $args >/dev/null 2>&1 done }
function run_auto_xfree () { eval "doit=\"\$AUTO_XFree\"" if [ "$doit" != yes -a "$1" != status ]; then return ; fi # (pixel) a kind of profile for XF86Config # if no XFree=XXX given on kernel command-line, restore XF86Config.standard for i in XF86Config XF86Config-4; do if [ -L "/etc/X11/$i" ]; then XFree=`sed -n 's/.*XFree=\(\w*\).*/\1/p' /proc/cmdline` [ -n "$XFree" ] || XFree=standard [ -r "/etc/X11/$i.$XFree" ] && ln -sf "$i.$XFree" "/etc/X11/$i" fi done action "bruby: configuring XFree." /bin/true }
function run_auto_dm () { eval "doit=\"\$AUTO_DM\"" if [ "$doit" != yes -a "$1" != status ]; then return ; fi # #the same like XF86Config but for gdm.conf & Xservers # for i in xdm/Xservers gdm/gdm.conf; do if [ -L "/etc/X11/$i" ]; then DumbCon=`sed -n 's/.*dumbcon=\([0-9]*\).*/\1/p' /proc/cmdline` [ -n "$DumbCon" ] || DumbCon=0 [ -r "/etc/X11/$i.$DumbCon" ] && ln -sf "/etc/X11/$i.$DumbCon" "/etc/X11/$i" fi done action "bruby: configuring display manager." /bin/true echo "Setting up display managers for `expr $DumbCon + 1` Xservers" }
function get_kbds() { j=0 for i in /proc/bus/console/* do echo j=`expr $j + 1` phys=`cat $i/keyboard` if [ "$phys" = "" ];then echo " VT-`basename $i` : keyboard not attached" else echo " VT-`basename $i` using :" # grep -n2 "$phys" /proc/bus/input/devices | sed -e '{/^B: /s;^B:.*;;};{s;^.*: ;;};' -e "/EV=/d" grep -B2 -A1 "$phys" /proc/bus/input/devices | sed 's;^.*: ;;' fi done echo echo "Total of $j VT's avialable." }
function get_links() { echo for i in /dev/input/$1*br do real=`ls -l $i 2>/dev/null | sed "{s;^.*/dev/input/;;};{s; ->;;}" | cut -d " " -f 2` if [ "$real" != "" ]; then echo "$i" sed -e '/Name/,/Handl/s/.*: //;{/^B: /s;^B:.*;;};' /proc/bus/input/devices | grep -B2 -A1 "$real" else exit 0 fi done }
if [ -f /proc/bus/console -o -n tmp=`uname -r | sed -n 's:ruby::p'` ]; then if [ -x /etc/hotplug/input.rc -a -f /etc/hotplug/input.agent ]; then
case "$1" in start|restart) run_start_restart ;; status) get_status touch /var/lock/subsys/ruby ;; ext-status) get_ext_status touch /var/lock/subsys/ruby ;; stop) #run_hackvideo stop run_input_rc stop rm -f /var/lock/subsys/ruby ;; force-reload) run_input_rc stop run_input_rc start touch /var/lock/subsys/ruby ;;
*) gprintf "Usage: %s {start|stop|restart|status|ext-status|force_reload}\n" "$0" exit 3 ;; esac
else if [ -f /etc/hotplug/input.rc -a -f /etc/hotplug/input.agent ]; then action "Input: input.rc and input.agent installed, but not executable." /bin/false action "Input: Please check the file permissions." /bin/false else action "Input: Failed to configure cold plugged devices." /bin/false action "Input: input.rc or input.agent missing." /bin/false fi fi fi

#!/bin/sh # Please place this file /etc/hotplug # # input-specific hotplug policy agent. # # Kernel Input params are: # # ACTION=add # PHYS=pysical location of device # NAME=Name of the device # # HISTORY: # 15-Sep-2003 modified to use single configuration file # /etc/sysconfig/ruby.conf by me :-) # (Svetoslav Slavtchev) # added handling for multimedia keys # but doesn't work as expected :( # 15-JUN-2003 removed paste # 07-MAY-2003 remake by Aivils Stoss # /proc manipulation added # parse kbd.conf event.conf mouse.conf. # create necessary symbolic links # 28-SEP-2002 Initial version from Andreas Schuldei # andreas (at) schuldei.org # cd /etc/hotplug . hotplug.functions DEBUG=yes export DEBUG
RUBY_CONF="/etc/sysconfig/ruby.conf"
setup_kbd () { while read VT_NUM PHYS_PATTERN NAME_PATTERN do if [ `echo "$VT_NUM" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then VT=`echo "$VT_NUM" | sed 's/VT//' | awk '{printf "%02d", $0}'` if [ -d /proc/bus/console/$VT ]; then echo "$PHYS" > "/proc/bus/console/$VT/keyboard" debug_mesg "Input device $NAME on $PHYS mapping as secondary to VT$VT" return; else debug_mesg "Trying to configure keyboard for VT$VT , but not enough VT's available" fi fi done debug_mesg "Found no fitting VT" }
setup_mm_keys () { while read VT_NUM PHYS_PATTERN NAME_PATTERN do if [ `echo "$VT_NUM" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then VT=`echo "$VT_NUM" | sed 's/VT//' | awk '{printf "%02d", $0}'` if [ -d /proc/bus/console/$VT ]; then echo "+$PHYS" > "/proc/bus/console/$VT/keyboard" debug_mesg "Input device $NAME on $PHYS mapping as secondary to VT$VT" return; else debug_mesg "Trying to configure keyboard for VT$VT , but not enough VT's available" fi fi done debug_mesg "Found no fitting VT" }
setup_event () { while read SYM_LINK PHYS_PATTERN NAME_PATTERN do if [ `echo "$SYM_LINK" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then case $ACTION in add) cd /dev/input rm -f $SYM_LINK ln -s $DEV_EVENT $SYM_LINK debug_mesg "Input event device $NAME on $PHYS linked to $SYM_LINK" ;; remove) rm -f /dev/input/$SYM_LINK debug_mesg "Input event device link $SYM_LINK removed" ;; esac return; fi done debug_mesg "Found no fitting event device" }
setup_mouse () { while read SYM_LINK PHYS_PATTERN NAME_PATTERN do if [ `echo "$SYM_LINK" | grep "^#"` ]; then continue; fi if [ `echo "$PHYS" | grep $PHYS_PATTERN 2>/dev/null` ]; then case $ACTION in add) cd /dev/input rm -f $SYM_LINK ln -s $DEV_MOUSE $SYM_LINK debug_mesg "Input mouse device $NAME on $PHYS linked to $SYM_LINK" ;; remove) rm -f /dev/input/$SYM_LINK debug_mesg "Input mouse device link $SYM_LINK removed" ;; esac return; fi done debug_mesg "Found no fitting mouse device" }
setup_input () { if [ -n "$DEV_KBD" ]; then sed -n "/keyboard devices/,/config/p" $RUBY_CONF | setup_kbd sed -n "/multimedia keys/,/config/p" $RUBY_CONF | setup_mm_keys fi if [ -n "$DEV_EVENT" ]; then sed -n "/event devices/,/config/p" $RUBY_CONF | setup_event fi if [ -n "$DEV_MOUSE" ]; then sed -n "/mouse devices/,/config/p" $RUBY_CONF | setup_mouse fi }
if [ "$ACTION" = "" ]; then mesg Bad input agent invocation exit 1 fi
DEV_HANDLERS=`grep -E 'Phys|Handlers' /proc/bus/input/devices | \ awk '{ if(count == 0) { printf("%s\t",$0); count++; } else { print $0; count=0; } }' | \ grep $PHYS | awk -F\t '{print $2}' | sed 's/^.*=//'`
DEV_EVENT=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /event/) print $n}'` DEV_MOUSE=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /mouse/) print $n}'` DEV_KBD=`echo $DEV_HANDLERS | \ awk -F" " '{for(n=1;$n;n=n+1) if($n ~ /kbd/) print $n}'`
# # What to do with this input device event? # case "$ACTION" in
add) setup_input ;; remove) #setup_input ;; *) debug_mesg "Input '$ACTION' event not supported" return 1 ;;
esac
